package com.rising.insta.src.dm;

import com.rising.insta.config.BaseException;
import com.rising.insta.config.BaseResponse;
import com.rising.insta.src.dm.dto.GetDMResp;
import com.rising.insta.utils.JwtService;
import lombok.RequiredArgsConstructor;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import java.time.LocalDateTime;
import java.util.ArrayList;
import java.util.List;

@RequiredArgsConstructor
@RestController
@RequestMapping("/app/dm")
public class DMController {

    private final JwtService jwtService;

    @GetMapping()
    public BaseResponse<?> dmList() {
        int idx;
        try {
            idx = jwtService.getUserId();
        } catch (BaseException e) {
            return new BaseResponse<>(e.getStatus());
        }

        List<GetDMResp> getDMRespList = new ArrayList<>();

        GetDMResp getDMResp1 = GetDMResp.builder()
                .userImg("data:image/jpeg;base64,")
                .name("마동석")
                .content("야 어디냐?")
                .time(LocalDateTime.now().toString())
                .build();
        getDMRespList.add(getDMResp1);

        GetDMResp getDMResp2 = GetDMResp.builder()
                .userImg("data:image/jpeg;base64,")
                .name("송중기")
                .content("뭐해?")
                .time(LocalDateTime.now().toString())
                .build();
        getDMRespList.add(getDMResp2);

        GetDMResp getDMResp3 = GetDMResp.builder()
                .userImg("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTml-NQvhP0IsBFFi4IGrxGGy83f8n39E4tjg&usqp=CAU")
                .name("alex")
                .content("OH! That's beautiful")
                .time(LocalDateTime.now().toString())
                .build();
        getDMRespList.add(getDMResp3);

        GetDMResp getDMResp4 = GetDMResp.builder()
                .userImg("data:image/jpeg;base64,")
                .name("고양이")
                .content("집사야 어디? 올 때 츄르~")
                .time(LocalDateTime.now().toString())
                .build();
        getDMRespList.add(getDMResp4);

        return new BaseResponse<>(getDMRespList);
    }
}
